import{_ as n,c as t,o as e,ae as i}from"./chunks/framework.BlRNOFgT.js";const k=JSON.parse('{"title":"Rangkuman Materi IF3140 – Introduction","description":"","frontmatter":{},"headers":[],"relativePath":"system-database/introduction.md","filePath":"system-database/introduction.md"}'),r={name:"system-database/introduction.md"};function s(l,a,o,g,u,d){return e(),t("div",null,[...a[0]||(a[0]=[i('<h1 id="rangkuman-materi-if3140-–-introduction" tabindex="-1">Rangkuman Materi IF3140 – Introduction <a class="header-anchor" href="#rangkuman-materi-if3140-–-introduction" aria-label="Permalink to &quot;Rangkuman Materi IF3140 – Introduction&quot;">​</a></h1><h2 id="_1-pengenalan-database-system" tabindex="-1">1. Pengenalan Database System <a class="header-anchor" href="#_1-pengenalan-database-system" aria-label="Permalink to &quot;1. Pengenalan Database System&quot;">​</a></h2><ul><li><strong>Database</strong> adalah kumpulan data yang saling terkait disimpan dalam satu repositori</li><li><strong>Keunggulan dibanding file system</strong>: <ul><li>Menghilangkan inkonsistensi, anomali data, ketergantungan data, dan ketergantungan struktural</li><li>Menyimpan struktur data, hubungan, dan jalur akses</li></ul></li><li><strong>Lingkungan Database</strong>: <ul><li>Hardware</li><li>Software</li><li>Pengguna</li><li>Prosedur (tata kelola)</li><li>Data</li></ul></li></ul><h3 id="contoh-soal-latihan" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Soal</strong>: Jelaskan mengapa database lebih unggul dibanding sistem file tradisional dalam mengelola data pegawai sebuah perusahaan.<br><strong>Jawaban</strong>: Database menghilangkan inkonsistensi dan anomali data, serta menyediakan akses terstruktur yang efisien, tidak seperti sistem file yang rentan terhadap duplikat data.</p></li><li><p><strong>Soal</strong>: Sebutkan dua komponen lingkungan database dan fungsinya.<br><strong>Jawaban</strong>: Hardware mendukung penyimpanan fisik data, sedangkan prosedur memastikan tata kelola data berjalan baik.</p></li><li><p><strong>Soal</strong>: Bagaimana database membantu dalam pengelolaan data pelanggan di toko online?<br><strong>Jawaban</strong>: Database menyimpan data pelanggan secara terorganisir, mengurangi ketergantungan data, dan memudahkan akses serta pembaruan informasi.</p></li></ol><hr><h2 id="_2-apa-itu-dbms" tabindex="-1">2. Apa Itu DBMS? <a class="header-anchor" href="#_2-apa-itu-dbms" aria-label="Permalink to &quot;2. Apa Itu DBMS?&quot;">​</a></h2><ul><li><strong>DBMS</strong> adalah perangkat lunak untuk menyimpan dan mengelola database</li><li><strong>Keunggulan DBMS</strong>: <ul><li>Memberikan lingkungan yang nyaman dan efisien</li><li>Kemandirian data dan akses efisien</li><li>Mengurangi waktu pengembangan aplikasi</li><li>Menjamin integritas dan keamanan data</li><li>Administrasi data seragam</li><li>Akses bersamaan dan pemulihan dari kerusakan</li></ul></li></ul><h3 id="contoh-soal-latihan-1" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-1" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Soal</strong>: Apa peran utama DBMS dalam sistem perpustakaan?<br><strong>Jawaban</strong>: DBMS menyimpan data buku dan peminjam, memastikan keamanan data, dan memungkinkan akses bersamaan.</p></li><li><p><strong>Soal</strong>: Bagaimana DBMS membantu mengurangi waktu pengembangan aplikasi?<br><strong>Jawaban</strong>: Dengan menyediakan lingkungan terstruktur, developer tidak perlu membangun sistem penyimpanan dari nol.</p></li><li><p><strong>Soal</strong>: Mengapa integritas data penting dalam DBMS untuk bank?<br><strong>Jawaban</strong>: Integritas data memastikan transaksi seperti transfer uang tetap akurat dan konsisten.</p></li></ol><hr><h2 id="_3-struktur-sistem-keseluruhan" tabindex="-1">3. Struktur Sistem Keseluruhan <a class="header-anchor" href="#_3-struktur-sistem-keseluruhan" aria-label="Permalink to &quot;3. Struktur Sistem Keseluruhan&quot;">​</a></h2><ul><li>Sistem database terbagi menjadi modul untuk menangani tugas masing-masing</li><li><strong>Komponen utama</strong>: <ul><li>Storage manager</li><li>Query processor component</li><li>Transaction management component</li></ul></li></ul><h3 id="contoh-soal-latihan-2" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-2" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Soal</strong>: Apa fungsi utama struktur sistem keseluruhan dalam database?<br><strong>Jawaban</strong>: Membagi tugas ke dalam modul seperti storage dan query untuk efisiensi.</p></li><li><p><strong>Soal</strong>: Sebutkan satu komponen dari struktur sistem dan kegunaannya.<br><strong>Jawaban</strong>: Storage manager mengelola penyimpanan dan pengambilan data.</p></li><li><p><strong>Soal</strong>: Bagaimana query processor mendukung pengguna database?<br><strong>Jawaban</strong>: Menginterpretasi perintah query untuk mengakses data dengan cepat.</p></li></ol><hr><h2 id="_4-database-engine" tabindex="-1">4. Database Engine <a class="header-anchor" href="#_4-database-engine" aria-label="Permalink to &quot;4. Database Engine&quot;">​</a></h2><ul><li><strong>Storage Manager</strong>: <ul><li>Antarmuka antara data tingkat rendah dan aplikasi</li><li>Tugas: Interaksi dengan file manager OS, penyimpanan/ambil/pembaruan data efisien</li><li>Komponen: Authorization/integrity manager, transaction manager, file manager, buffer manager</li><li>Struktur data: Data files (simpan database), data dictionary (metadata), indices (akses cepat)</li></ul></li><li><strong>Query Processor</strong>: <ul><li>Komponen: DDL interpreter, DML compiler, query evaluation engine</li><li>Proses: Parsing/terjemahan, optimasi, evaluasi</li></ul></li><li><strong>Transaction Management</strong>: <ul><li>Mengelola transaksi untuk menjaga konsistensi data</li><li>Mengatasi kegagalan sistem dan kontrol konkurensi</li></ul></li></ul><h3 id="tabel-perbandingan" tabindex="-1">Tabel Perbandingan <a class="header-anchor" href="#tabel-perbandingan" aria-label="Permalink to &quot;Tabel Perbandingan&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Komponen</th><th>Fungsi Utama</th><th>Contoh Tugas</th></tr></thead><tbody><tr><td>Storage Manager</td><td>Kelola penyimpanan data</td><td>Simpan dan ambil data</td></tr><tr><td>Query Processor</td><td>Proses dan optimasi query</td><td>Terjemahkan perintah SQL</td></tr><tr><td>Transaction Management</td><td>Jaga konsistensi transaksi</td><td>Pulihkan data setelah crash</td></tr></tbody></table><h3 id="contoh-soal-latihan-3" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-3" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Soal</strong>: Jelaskan peran storage manager dalam sistem e-commerce.<br><strong>Jawaban</strong>: Menyimpan data produk dan pesanan, serta memastikan akses cepat via indices.</p></li><li><p><strong>Soal</strong>: Apa yang dilakukan query processor saat pengguna menjalankan query?<br><strong>Jawaban</strong>: Menerjemahkan query ke rencana evaluasi dan mengoptimalkan biaya eksekusi.</p></li><li><p><strong>Soal</strong>: Bagaimana transaction management menjaga data bank?<br><strong>Jawaban</strong>: Memastikan transfer uang konsisten meski ada kegagalan sistem.</p></li></ol><hr><h2 id="_5-aplikasi-dan-pengguna-database" tabindex="-1">5. Aplikasi dan Pengguna Database <a class="header-anchor" href="#_5-aplikasi-dan-pengguna-database" aria-label="Permalink to &quot;5. Aplikasi dan Pengguna Database&quot;">​</a></h2><ul><li><strong>Arsitektur</strong>: <ul><li>Two-tier: Aplikasi di klien, panggil fungsi server</li><li>Three-tier: Klien sebagai frontend, server aplikasi sebagai perantara</li></ul></li><li><strong>Database Administrator (DBA)</strong>: <ul><li>Kontrol pusat sistem</li><li>Tugas: Definisikan skema, atur penyimpanan, beri otorisasi, perawatan rutin</li></ul></li></ul><h3 id="contoh-soal-latihan-4" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-4" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Soal</strong>: Apa perbedaan arsitektur two-tier dan three-tier?<br><strong>Jawaban</strong>: Two-tier langsung panggil server, three-tier gunakan server aplikasi sebagai perantara.</p></li><li><p><strong>Soal</strong>: Sebutkan dua tugas DBA dalam sistem rumah sakit.<br><strong>Jawaban</strong>: Definisikan skema data pasien dan beri otorisasi akses dokter.</p></li><li><p><strong>Soal</strong>: Bagaimana three-tier membantu aplikasi online?<br><strong>Jawaban</strong>: Memisahkan frontend dari database untuk skalabilitas lebih baik.</p></li></ol><hr><h2 id="daftar-istilah-penting" tabindex="-1">Daftar Istilah Penting <a class="header-anchor" href="#daftar-istilah-penting" aria-label="Permalink to &quot;Daftar Istilah Penting&quot;">​</a></h2><ul><li><strong>Database</strong>: Kumpulan data terorganisir dalam satu tempat</li><li><strong>DBMS</strong>: Perangkat lunak untuk mengelola database</li><li><strong>Storage Manager</strong>: Modul yang mengelola penyimpanan dan pengambilan data</li><li><strong>Query Processor</strong>: Komponen yang memproses dan mengoptimasi query</li><li><strong>Transaction Management</strong>: Sistem untuk menjaga konsistensi transaksi</li><li><strong>Data Dictionary</strong>: Tempat menyimpan metadata tentang struktur database</li><li><strong>Indices</strong>: Struktur untuk akses cepat ke data tertentu</li><li><strong>DDL</strong>: Bahasa definisi data untuk membuat skema</li><li><strong>DML</strong>: Bahasa manipulasi data untuk query dan pembaruan</li></ul><h2 id="glossarium" tabindex="-1">Glossarium <a class="header-anchor" href="#glossarium" aria-label="Permalink to &quot;Glossarium&quot;">​</a></h2><ul><li><strong>DBMS</strong>: Database Management System</li><li><strong>DDL</strong>: Data Definition Language</li><li><strong>DML</strong>: Data Manipulation Language</li></ul>',32)])])}const p=n(r,[["render",s]]);export{k as __pageData,p as default};
