import{_ as t,c as i,o as n,ae as e}from"./chunks/framework.BlRNOFgT.js";const h=JSON.parse('{"title":"Rangkuman Materi IF3140 – Storage and File Structure","description":"","frontmatter":{},"headers":[],"relativePath":"system-database/storage-and-file.md","filePath":"system-database/storage-and-file.md"}'),r={name:"system-database/storage-and-file.md"};function l(s,a,o,u,d,k){return n(),i("div",null,[...a[0]||(a[0]=[e('<h1 id="rangkuman-materi-if3140-–-storage-and-file-structure" tabindex="-1">Rangkuman Materi IF3140 – Storage and File Structure <a class="header-anchor" href="#rangkuman-materi-if3140-–-storage-and-file-structure" aria-label="Permalink to &quot;Rangkuman Materi IF3140 – Storage and File Structure&quot;">​</a></h1><h2 id="_1-klasifikasi-media-penyimpanan-fisik" tabindex="-1">1. Klasifikasi Media Penyimpanan Fisik <a class="header-anchor" href="#_1-klasifikasi-media-penyimpanan-fisik" aria-label="Permalink to &quot;1. Klasifikasi Media Penyimpanan Fisik&quot;">​</a></h2><ul><li><strong>Jenis penyimpanan:</strong><ul><li>Penyimpanan volatil: hilang saat listrik mati</li><li>Penyimpanan non-volatil: data tetap meski listrik mati, termasuk penyimpanan sekunder, tersier, dan memori utama dengan cadangan baterai</li></ul></li><li><strong>Faktor pemilihan media penyimpanan:</strong><ul><li>Kecepatan akses data</li><li>Biaya per unit data</li><li>Reliabilitas</li></ul></li></ul><h3 id="seluruh-tabel-perbandingan" tabindex="-1">Seluruh Tabel Perbandingan <a class="header-anchor" href="#seluruh-tabel-perbandingan" aria-label="Permalink to &quot;Seluruh Tabel Perbandingan&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Kriteria</th><th>Volatil</th><th>Non-Volatil</th></tr></thead><tbody><tr><td>Ketahanan Data</td><td>Hilang saat mati</td><td>Tetap saat mati</td></tr><tr><td>Contoh</td><td>Cache, memori utama</td><td>Disk, tape</td></tr><tr><td>Penggunaan</td><td>Sementara</td><td>Jangka panjang</td></tr></tbody></table><h3 id="contoh-soal-latihan" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Sebuah perusahaan memilih penyimpanan untuk data sensitif yang harus tetap aman meski listrik padam. Media apa yang paling sesuai dan mengapa?<br><strong>Jawaban:</strong> Penyimpanan non-volatil, karena data tetap meski listrik mati, cocok untuk data sensitif.</p></li><li><p><strong>Studi Kasus:</strong> Jika biaya menjadi prioritas utama, media penyimpanan apa yang sebaiknya dipilih?<br><strong>Jawaban:</strong> Penyimpanan tersier seperti tape, karena biaya per unit datanya lebih murah.</p></li><li><p><strong>Studi Kasus:</strong> Sebuah aplikasi membutuhkan akses data sangat cepat. Media apa yang tepat?<br><strong>Jawaban:</strong> Penyimpanan volatil seperti cache, karena kecepatan aksesnya tinggi.</p></li></ol><hr><h2 id="_2-mekanisme-disk-magnetik" tabindex="-1">2. Mekanisme Disk Magnetik <a class="header-anchor" href="#_2-mekanisme-disk-magnetik" aria-label="Permalink to &quot;2. Mekanisme Disk Magnetik&quot;">​</a></h2><ul><li><strong>Struktur disk:</strong><ul><li>Permukaan piringan dibagi menjadi trek melingkar</li><li>Setiap trek dibagi menjadi sektor (unit terkecil data yang bisa dibaca/tulis)</li></ul></li><li><strong>Komponen penting:</strong><ul><li>Kepala baca-tulis (read-write head)</li><li>Perakitan kepala-disk</li><li>Silinder i: trek ke-i dari semua piringan</li></ul></li></ul><h3 id="contoh-soal-latihan-1" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-1" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Jika sebuah disk memiliki 10 piringan dan kepala berada di trek ke-5, berapa jumlah silinder yang terlibat?<br><strong>Jawaban:</strong> 1 silinder, karena silinder mencakup trek ke-i dari semua piringan.</p></li><li><p><strong>Studi Kasus:</strong> Sektor pada disk rusak. Bagaimana sistem menanganinya?<br><strong>Jawaban:</strong> Disk controller melakukan remapping sektor rusak ke area cadangan.</p></li><li><p><strong>Studi Kasus:</strong> Data disimpan di sektor tertentu. Apa unit terkecil yang bisa diakses?<br><strong>Jawaban:</strong> Sektor, karena itu unit terkecil untuk baca/tulis.</p></li></ol><hr><h2 id="_3-sub-sistem-disk" tabindex="-1">3. Sub Sistem Disk <a class="header-anchor" href="#_3-sub-sistem-disk" aria-label="Permalink to &quot;3. Sub Sistem Disk&quot;">​</a></h2><ul><li><strong>Fungsi disk controller:</strong><ul><li>Menghubungkan sistem komputer dengan hardware disk</li><li>Menerima perintah tingkat tinggi untuk baca/tulis sektor</li><li>Menggerakkan lengan disk dan membaca/menulis data</li><li>Menghitung checksum untuk verifikasi data</li><li>Meremapping sektor rusak</li></ul></li><li><strong>Koneksi:</strong> Banyak disk terhubung ke sistem melalui disk controller</li></ul><h3 id="contoh-soal-latihan-2" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-2" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Data yang dibaca ternyata korup. Apa yang dilakukan disk controller?<br><strong>Jawaban:</strong> Memverifikasi dengan checksum dan meremapping sektor jika rusak.</p></li><li><p><strong>Studi Kasus:</strong> Sebuah server memiliki 5 disk. Bagaimana disk dikendalikan?<br><strong>Jawaban:</strong> Melalui disk controller yang menghubungkan semua disk ke sistem.</p></li><li><p><strong>Studi Kasus:</strong> Perintah untuk menulis data diterima. Apa langkah awal?<br><strong>Jawaban:</strong> Disk controller menggerakkan lengan ke trek yang tepat.</p></li></ol><hr><h2 id="_4-ukuran-kinerja-disk" tabindex="-1">4. Ukuran Kinerja Disk <a class="header-anchor" href="#_4-ukuran-kinerja-disk" aria-label="Permalink to &quot;4. Ukuran Kinerja Disk&quot;">​</a></h2><ul><li><strong>Waktu akses:</strong> Waktu dari perintah hingga transfer data dimulai <ul><li><strong>Seek time:</strong> Waktu memindahkan lengan ke trek yang benar</li><li><strong>Rotational latency:</strong> Waktu sektor muncul di bawah kepala</li></ul></li><li><strong>Data-transfer rate:</strong> Kecepatan ambil/simpan data</li><li><strong>Mean time to failure (MTTF):</strong> Rata-rata waktu disk berjalan tanpa gagal <ul><li>Biasanya 3-5 tahun, menurun seiring usia disk</li></ul></li></ul><h3 id="seluruh-tabel-perbandingan-1" tabindex="-1">Seluruh Tabel Perbandingan <a class="header-anchor" href="#seluruh-tabel-perbandingan-1" aria-label="Permalink to &quot;Seluruh Tabel Perbandingan&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Parameter</th><th>Penjelasan</th><th>Contoh Nilai</th></tr></thead><tbody><tr><td>Seek Time</td><td>Waktu geser lengan</td><td>4-10 milidetik</td></tr><tr><td>Rotational Latency</td><td>Waktu putar sektor</td><td>4-11 milidetik</td></tr><tr><td>Data-Transfer Rate</td><td>Kecepatan transfer data</td><td>25-200 MB/detik</td></tr><tr><td>MTTF</td><td>Waktu rata-rata tanpa gagal</td><td>3-5 tahun</td></tr></tbody></table><h3 id="contoh-soal-latihan-3" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-3" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Disk memiliki seek time 6 ms dan rotational latency 5 ms. Berapa waktu akses minimum?<br><strong>Jawaban:</strong> 11 ms (6 ms + 5 ms).</p></li><li><p><strong>Studi Kasus:</strong> Disk tua menunjukkan penurunan kinerja. Apa yang mungkin terjadi?<br><strong>Jawaban:</strong> MTTF menurun karena usia disk meningkat.</p></li><li><p><strong>Studi Kasus:</strong> Data transfer rate 100 MB/detik digunakan. Berapa waktu untuk transfer 1 GB?<br><strong>Jawaban:</strong> 10 detik (1000 MB / 100 MB/detik).</p></li></ol><hr><h2 id="_5-akses-blok-disk" tabindex="-1">5. Akses Blok Disk <a class="header-anchor" href="#_5-akses-blok-disk" aria-label="Permalink to &quot;5. Akses Blok Disk&quot;">​</a></h2><ul><li><strong>Blok:</strong> Unit penyimpanan dan transfer data, berupa urutan sektor <ul><li>Ukuran: 512 byte hingga beberapa kilobyte</li><li>Blok kecil: lebih banyak transfer, ruang terbuang</li><li>Blok besar: ruang terbuang lebih sedikit</li><li>Ukuran tipikal: 4-16 kilobyte</li></ul></li><li><strong>Akses:</strong> Jauh lebih lambat daripada memori utama</li><li><strong>Optimasi:</strong> Minimalkan transfer blok dengan menyimpan blok di memori utama</li></ul><h3 id="contoh-soal-latihan-4" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-4" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Blok berukuran 4 KB digunakan. Berapa data yang bisa disimpan jika sektor 512 byte?<br><strong>Jawaban:</strong> 8 sektor (4000 / 512).</p></li><li><p><strong>Studi Kasus:</strong> Akses disk 1000 kali lebih lambat dari memori. Bagaimana mengoptimalkan?<br><strong>Jawaban:</strong> Simpan blok di memori utama untuk kurangi akses disk.</p></li><li><p><strong>Studi Kasus:</strong> Blok 16 KB separuh terisi. Berapa ruang terbuang?<br><strong>Jawaban:</strong> 8 KB.</p></li></ol><hr><h2 id="_6-organisasi-file" tabindex="-1">6. Organisasi File <a class="header-anchor" href="#_6-organisasi-file" aria-label="Permalink to &quot;6. Organisasi File&quot;">​</a></h2><ul><li><strong>Dasar:</strong><ul><li>Database disimpan sebagai kumpulan file</li><li>Setiap file berisi urutan record</li><li>Record berisi urutan field</li></ul></li><li><strong>Blocking:</strong><ul><li>Record lebih kecil dari blok</li><li>Bfr (blocking factor) = [B/R] (B: ukuran blok, R: ukuran record)</li><li>Bisa spanned (melintasi blok) atau unspanned (tidak melintasi)</li></ul></li></ul><h3 id="seluruh-tabel-perbandingan-2" tabindex="-1">Seluruh Tabel Perbandingan <a class="header-anchor" href="#seluruh-tabel-perbandingan-2" aria-label="Permalink to &quot;Seluruh Tabel Perbandingan&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Jenis Blocking</th><th>Kelebihan</th><th>Kekurangan</th></tr></thead><tbody><tr><td>Unspanned</td><td>Sederhana, tidak melintasi</td><td>Ruang terbuang jika tidak penuh</td></tr><tr><td>Spanned</td><td>Efisien ruang</td><td>Kompleks untuk akses</td></tr></tbody></table><h3 id="contoh-soal-latihan-5" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-5" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Blok 4 KB, record 1 KB. Berapa Bfr untuk unspanned?<br><strong>Jawaban:</strong> 4 (4000 / 1000).</p></li><li><p><strong>Studi Kasus:</strong> File memiliki 10 record 500 byte, blok 2 KB. Apakah spanned diperlukan?<br><strong>Jawaban:</strong> Ya, karena 500 byte x 10 = 5000 byte &gt; 2 KB.</p></li><li><p><strong>Studi Kasus:</strong> Data disimpan dalam file. Apa unit dasarnya?<br><strong>Jawaban:</strong> Record, yang berisi field.</p></li></ol><hr><h2 id="_7-organisasi-record-dalam-file" tabindex="-1">7. Organisasi Record dalam File <a class="header-anchor" href="#_7-organisasi-record-dalam-file" aria-label="Permalink to &quot;7. Organisasi Record dalam File&quot;">​</a></h2><ul><li><strong>Jenis organisasi:</strong><ul><li><strong>Heap:</strong> Record ditempatkan sembarang di ruang kosong</li><li><strong>Sequential:</strong> Record diurutkan berdasarkan kunci pencarian</li><li><strong>Multitable clustering:</strong> Beberapa relasi disimpan dalam satu file</li><li><strong>B+-tree:</strong> Penyimpanan terurut meski ada insert/delete</li><li><strong>Hashing:</strong> Fungsi hash menentukan blok penyimpanan</li></ul></li></ul><h3 id="seluruh-tabel-perbandingan-3" tabindex="-1">Seluruh Tabel Perbandingan <a class="header-anchor" href="#seluruh-tabel-perbandingan-3" aria-label="Permalink to &quot;Seluruh Tabel Perbandingan&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Jenis Organisasi</th><th>Kelebihan</th><th>Kekurangan</th></tr></thead><tbody><tr><td>Heap</td><td>Efisien untuk bulk loading</td><td>Lambat untuk pencarian</td></tr><tr><td>Sequential</td><td>Bagus untuk proses berurutan</td><td>Perlu reorganisasi</td></tr><tr><td>Multitable</td><td>Minimalkan I/O</td><td>Kurang efisien untuk satu relasi</td></tr></tbody></table><h3 id="contoh-soal-latihan-6" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-6" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Data sering ditambahkan dalam jumlah besar. Organisasi apa yang cocok?<br><strong>Jawaban:</strong> Heap, karena efisien untuk bulk loading.</p></li><li><p><strong>Studi Kasus:</strong> File perlu diurutkan berdasarkan ID. Apa yang digunakan?<br><strong>Jawaban:</strong> Sequential, untuk penyimpanan terurut.</p></li><li><p><strong>Studi Kasus:</strong> Dua tabel terkait disimpan bersama. Organisasi apa yang tepat?<br><strong>Jawaban:</strong> Multitable clustering, untuk minimalkan I/O.</p></li></ol><hr><h2 id="_8-penyimpanan-data-dictionary" tabindex="-1">8. Penyimpanan Data Dictionary <a class="header-anchor" href="#_8-penyimpanan-data-dictionary" aria-label="Permalink to &quot;8. Penyimpanan Data Dictionary&quot;">​</a></h2><ul><li><strong>Isi data dictionary:</strong><ul><li>Informasi relasi (nama, tipe atribut, panjang)</li><li>Definisi view dan batasan integritas</li><li>Data statistik (jumlah tuple)</li><li>Informasi fisik (lokasi, indeks)</li></ul></li></ul><h3 id="contoh-soal-latihan-7" tabindex="-1">Contoh Soal Latihan <a class="header-anchor" href="#contoh-soal-latihan-7" aria-label="Permalink to &quot;Contoh Soal Latihan&quot;">​</a></h3><ol><li><p><strong>Studi Kasus:</strong> Sistem membutuhkan metadata relasi. Di mana disimpan?<br><strong>Jawaban:</strong> Data dictionary.</p></li><li><p><strong>Studi Kasus:</strong> Jumlah tuple diperlukan untuk optimasi. Sumbernya apa?<br><strong>Jawaban:</strong> Data dictionary.</p></li><li><p><strong>Studi Kasus:</strong> Lokasi fisik file hilang. Bagaimana menemukannya?<br><strong>Jawaban:</strong> Cek data dictionary.</p></li></ol><hr><h2 id="daftar-istilah-penting" tabindex="-1">Daftar Istilah Penting <a class="header-anchor" href="#daftar-istilah-penting" aria-label="Permalink to &quot;Daftar Istilah Penting&quot;">​</a></h2><ul><li><strong>Volatile Storage:</strong> Penyimpanan yang hilang saat listrik mati, seperti cache.</li><li><strong>Non-Volatile Storage:</strong> Penyimpanan yang data tetap meski listrik mati, seperti disk.</li><li><strong>Seek Time:</strong> Waktu untuk memindahkan lengan disk ke trek yang benar.</li><li><strong>Rotational Latency:</strong> Waktu sektor muncul di bawah kepala baca.</li><li><strong>MTTF:</strong> Rata-rata waktu disk berjalan tanpa gagal.</li><li><strong>Blok:</strong> Unit penyimpanan dan transfer data di disk.</li><li><strong>Record:</strong> Urutan field yang membentuk data dalam file.</li><li><strong>Blocking Factor (Bfr):</strong> Jumlah record dalam satu blok.</li><li><strong>Disk Controller:</strong> Perangkat yang menghubungkan disk ke sistem komputer.</li><li><strong>Cylinder:</strong> Kumpulan trek ke-i dari semua piringan disk.</li></ul><h2 id="glossarium" tabindex="-1">Glossarium <a class="header-anchor" href="#glossarium" aria-label="Permalink to &quot;Glossarium&quot;">​</a></h2><ul><li><strong>MTTF:</strong> Mean Time To Failure</li></ul>',53)])])}const m=t(r,[["render",l]]);export{h as __pageData,m as default};
